mapper.select:
SELECT COUNT(*) 
  FROM employee_tbl 
 WHERE employee_id = #{employee_id}

SELECT COUNT(*) 
  FROM Auth_tbl 
 WHERE ID = #{ID}

mapper.update:
UPDATE attendance_tbl
   SET check_out_time = #{check_out_time}
 WHERE employee_code = #{employee_code}
   AND TO_CHAR(attendance_date, 'yyyy/MM/dd') = TO_CHAR(#{attendance_date}, 'yyyy/MM/dd')
